package week1.day1;

public class LearnContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      for (int i = 10; i >=1; i--) {
    	  if(i==7)
    	  {
    		  System.out.println("Seven");
    		  continue;
    		  
    	  }
		System.out.println(i);
	}
	}

}
